import React from 'react';
import './HomePage.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import DragonBase from './../../components/Tables/DragonBase'
import HeaderButton from './../../components/Buttons/HeaderButton';
import BaseInput from './../../components/Inputs/BaseInput';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useRouteMatch,
  useParams,
} from 'react-router-dom';
import { useState, useEffect } from 'react';




function DragonList() {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Примечание: пустой массив зависимостей [] означает, что
  // этот useEffect будет запущен один раз
  // аналогично componentDidMount()
  useEffect(() => {
      fetch("/dragons")
          .then(res => res.json())
          .then(
              (result) => {
                  setIsLoaded(true);
                  setItems(result);
              },
              // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
              // чтобы не перехватывать исключения из ошибок в самих компонентах.
              (error) => {
                  setIsLoaded(true);
                  setError(error);
              }
          )
  }, [])

  if (error) {
      return <div>Ошибка: {error.message}</div>;
  } else if (!isLoaded) {
      return <div>Загрузка...</div>;
  } else {
      return (
          <ul>
              {items.map(item => (
                  <li key={item.id}>
                      {item.title}
                  </li>
              ))}
          </ul>
      );
  }

}

function HomePage()
{
  return (
    <div>
    <main>
    <DragonList/>
    <DragonBase rows={rows} headers={headers}  page="1 из я хуй знает скольки" />
    </main>
    <footer>
    <ul className='NeededOperations'>
    <li>
    <BaseInput 
    name="Массив драконов, имя которых начинается с:" 
    enter="Введите подстроку имени"
    />
    </li>
    <li><Link to="/returnarray"><HeaderButton name="Вернуть"/></Link></li>
    </ul>
    <ul className='NeededOperations'> 
    <li>
    <BaseInput 
    name="Количество драконов, характер которых больше:" 
    enter="Введите характер"
    />
    </li>
    <li><Link to='/charactercount'> <HeaderButton name="Вернуть"/></Link> </li>
    </ul>
    <ul className='NeededOperations'>
    <li>
    <BaseInput 
    name="Количество драконов,тип которых меньше:" 
    enter="Введите тип"
    />
    </li>
    <li><Link to='/typecount'> <HeaderButton name="Вернуть"/> </Link></li>
    </ul> 
    </footer>
    </div>
  );
}

export default HomePage;
