import './DragonBase.css'
import leftArrow from './left-arrow.png'
import rightArrow from './right-arrow.png'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table'
import React from 'react';

function showTotal() {
  return <p>There are 100 items total</p>
}

class DragonBase extends React.Component {
  render() {
    const options = {
      page: 4,
      prePage:  '⟵',
      nextPage: '⟶',
      firstPage: '⟸',
      lastPage: '⟹',
      paginationShowsTotal: showTotal
    }
    
    return (
      <BootstrapTable
      pagination={true}
      options={options}>
          <thead>
            {this.props.headers.map((header, i) => 
              <th scope="col" key={i} >
                {header}
                </th>
            )}
          </thead>
          <tbody>
          {this.props.rows.map((row, i) => 
            <tr key={i}>
              {row.map((cell, i) =>
                (this.props.rowHeaders && i < 1) ? (
                  <th scope="row" key={i}>{cell}</th>
                ) : (
                  <td key={i}>{cell}</td>
                )
              )}
            </tr>
           )}
        </tbody>
        <tfoot>
              <th>{this.props.page}</th>
              <th><img src={leftArrow} className="Arrow" alt="to left" /></th>
              <th><img src={rightArrow} className="Arrow" alt="to right" /></th>
          </tfoot>
      </BootstrapTable>
    );
  }
}


export default DragonBase;
